import React, { useState, useEffect } from 'react';
import { Button, Container, Typography, Box, List, ListItem, ListItemText, IconButton } from '@mui/material';
import axios from 'axios';
import DownloadIcon from '@mui/icons-material/Download';

function Dashboard({ token, onLogout }) {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchFiles();
    // eslint-disable-next-line
  }, []);

  const fetchFiles = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/files', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFiles(res.data);
    } catch (err) {
      setError('Failed to fetch files');
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append('file', selectedFile);
    try {
      await axios.post('http://localhost:5000/api/files/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      setSelectedFile(null);
      fetchFiles();
    } catch (err) {
      setError('Upload failed');
    }
  };


  const handleDownload = async (filename) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/files/${filename}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      // Create a link to download the file
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (err) {
      setError('Download failed');
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 4, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Typography variant="h4" gutterBottom>Dashboard</Typography>
        <Button variant="outlined" color="secondary" onClick={onLogout} sx={{ mb: 2 }}>Logout</Button>
        <Box sx={{ mb: 2 }}>
          <input type="file" onChange={handleFileChange} />
          <Button variant="contained" onClick={handleUpload} disabled={!selectedFile} sx={{ ml: 2 }}>Upload</Button>
        </Box>
        {error && <Typography color="error">{error}</Typography>}
        <Typography variant="h6" sx={{ mt: 2 }}>Your Files</Typography>
        <List>
          {files.map(file => (
            <ListItem key={file._id} secondaryAction={
              <IconButton edge="end" onClick={() => handleDownload(file.filename)}>
                <DownloadIcon />
              </IconButton>
            }>
              <ListItemText primary={file.originalname} />
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default Dashboard;
